(function executeRule(current, previous /*null when async*/) {

	gs.addInfoMessage("Regex - Fix Question Ran");
	var htmlFix = current.question;
	var decodeHTML = new x_275551_trivia_ga.Trivia_Game_Regex_Utils().decodeHTML(htmlFix);
	current.question = decodeHTML;
	current.update();

})(current, previous);

